func ConstructValidationContract(contract, actingParty string) string {
	return fmt.Sprintf(`{
	"contract_format": "irma",
    "contract_string": "%s",
	"acting_party_cn": "%s"
}`, base64.StdEncoding.EncodeToString([]byte(contract)), actingParty)
}

